REPLACE INTO creature_text VALUES
('44086', '0', '0', 'What\'s going on? I thought I saw something on the rooftops.', '12', '0', '100', '0', '0', '0', ''),
('44086', '1', '0', 'My children are in Stormglen alone! I have to get to them!', '12', '0', '100', '0', '0', '0', ''),
('44086', '2', '0', 'You can\'t do this to us!', '12', '0', '100', '0', '0', '0', ''),
('44086', '3', '0', 'What... what are those things on the rooftops?', '12', '0', '100', '0', '0', '0', ''),
('34850', '0', '0', 'I want the perimeter secured and the gates manned by two guards at all times. No one gets in, no one gets out.', '12', '0', '100', '0', '0', '19615', 'SAY_PRINCE_LIAM_GREYMANE_1 - Say OOC'),
('34850', '1', '0', 'We protected Gilneas from the Scourge. We protected Gilneas during the Northgate rebellion. We will protect Gilneas from whatever this new threat may be.', '12', '0', '100', '0', '0', '19616', 'SAY_PRINCE_LIAM_GREYMANE_2 - Say OOC'),
('34850', '2', '0', 'Stand ready, guards! We don\'t know how many intruders we\'re dealing with, but the Headlands are overrun and we\'re cut off from the harbor towns. Expect to be outnumbered.', '12', '0', '100', '0', '0', '19614', 'SAY_PRINCE_LIAM_GREYMANE_3 - Say OOC'),
('34913', '0', '0', 'Stand your ground, men!', '12', '0', '100', '0', '0', '19617', 'SAY_PRINCE_LIAM_GREYMANE - Phase 2 OOC'),
('34913', '1', '0', 'Defeat these foul beasts!', '12', '0', '100', '0', '0', '19618', 'SAY_PRINCE_LIAM_GREYMANE - Phase 2 OOC'),
('34913', '2', '0', 'Protect the civilians!', '12', '0', '100', '0', '0', '19619', 'SAY_PRINCE_LIAM_GREYMANE - Phase 2 OOC'),
('34913', '3', '0', 'Push them back!', '12', '0', '100', '0', '0', '19620', 'SAY_PRINCE_LIAM_GREYMANE - Phase 2 OOC'),
('34913', '4', '0', 'Take heart, men! We must protect our city!', '12', '0', '100', '0', '0', '19621', 'SAY_PRINCE_LIAM_GREYMANE - Phase 2 OOC'),
('34864', '0', '0', 'No one\'s allowed to leave. Prince Liam\'s orders.', '12', '0', '100', '0', '0', '0', 'SAY_GILNEAS_CITY_GUARD_1 - At gate'),
('34864', '1', '0', 'Stay calm. We have everything under control.', '12', '0', '100', '0', '0', '0', 'SAY_GILNEAS_CITY_GUARD_2 - At gate'),
('34864', '2', '0', 'It isn\'t safe to leave, there\'s something lurking out there in the woods.', '12', '0', '100', '0', '0', '0', 'SAY_GILNEAS_CITY_GUARD_3 - At gate'),
('34981', '0', '0', 'Protect me, please!', '12', '0', '100', '0', '0', '0', ''),
('34981', '1', '0', 'What in the world? Let\'s get out of here!', '12', '0', '100', '0', '0', '0', ''),
('34981', '2', '0', 'Worgen! Worgen everywhere!', '12', '0', '100', '0', '0', '0', ''),
('34981', '3', '0', 'Flee! They\'re everywhere!', '12', '0', '100', '0', '0', '0', ''),
('34981', '4', '0', 'No time to Waste!', '12', '0', '100', '0', '0', '0', ''),
('34981', '5', '0', 'This place isn\'t safe. Let\'s Leave!', '12', '0', '100', '0', '0', '0', ''),
('34981', '6', '0', 'Let\'s go!', '12', '0', '100', '0', '0', '0', ''),
('34981', '7', '0', 'Thank you!', '12', '0', '100', '0', '0', '0', ''),
('35836', '0', '0', 'I don\'t want to die!', '12', '0', '100', '0', '0', '0', ''),
('35836', '1', '0', 'There\'s one after me!', '12', '0', '100', '0', '0', '0', ''),
('35836', '2', '0', 'It\'s coming right for me!', '12', '0', '100', '0', '0', '0', ''),
('35836', '3', '0', 'Help!', '12', '0', '100', '0', '0', '0', ''),
('35836', '4', '0', 'Help me, please!', '12', '0', '100', '0', '0', '0', '');
REPLACE INTO `script_texts` (`npc_entry`, `entry`, `content_default`, `content_loc1`, `content_loc2`, `content_loc3`, `content_loc4`, `content_loc5`, `content_loc6`, `content_loc7`, `content_loc8`, `sound`, `type`, `language`, `emote`, `comment`) VALUES
('50474', '-1977161', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Не смотрите им в глаза! Они не те люди, которых вы когда-то знали!', '0', '1', '0', '0', 'gilneas'),
('35550', '-1977158', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Огонь!', '0', '1', '0', '0', 'gilneas'),
('35550', '-1977159', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Нужно их отбросить!', '0', '1', '0', '0', 'gilneas'),
('34864', '-1977156', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Выход закрыт. Приказ принца Лиама!', '0', '0', '0', '0', 'gilneas'),
('34864', '-1977157', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'В лесах творится что-то странное, и выходить за пределы города небезопасно.', '0', '0', '0', '0', 'gilneas'),
('35550', '-1977160', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Не сдавайте баррикады! Не уступайте им ни клочка земли!', '0', '1', '0', '0', 'gilneas'),
('34864', '-1977155', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Всем сохранять спокойствие! Мы контролируем ситуацию.', '0', '0', '0', '0', 'gilneas'),
('44086', '-1977154', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Мои дети остались в Грозовом Перевали, совсем одни! Я должна к ним вернуться!', '0', '0', '0', '0', 'gilneas'),
('50902', '-1977151', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Гилнеас не забудет твоего мужества, Лиам.', '19497', '12', '0', '0', 'gilneas'),
('44086', '-1977152', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Вы не можете так поступить с нами!', '0', '0', '0', '0', 'gilneas'),
('44086', '-1977153', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Что происходит? Кажется, я видел что-то на крише.', '0', '0', '0', '0', 'gilneas'),
('50893', '-1977149', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Мы вернемся, Лиам. Клянусь.', '19715', '12', '0', '0', 'gilneas'),
('50881', '-1977150', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ты был настоящим народным героем, Лиам. Не таким, как другие принцы. Они заплатят за твою смерть.', '19686', '12', '0', '0', 'gilneas'),
('50893', '-1977147', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Свет да пребудет с духами наших предков, ибо они позволили моему сыну покоиться в этой священной земле.', '19713', '12', '0', '0', 'gilneas'),
('50893', '-1977148', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Его место здесь, среди героев и защитников Гилнеаса.', '19714', '12', '0', '0', 'gilneas'),
('36288', '-1977145', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'А вы – хороший ворген, $Gсэр:мэм;? Может быть, вы найдете Синтию? Она прячется где-то тут!', '0', '0', '0', '0', 'gilneas'),
('36289', '-1977146', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Не трогай меня! Я просто искал сестренку! Кажется, Эшли все еще в том доме!', '0', '0', '0', '0', 'gilneas'),
('36287', '-1977144', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Я тебя боюсь! Где моя мама?', '0', '0', '0', '0', 'gilneas'),
('36440', '-1977143', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Благодарю, я обязан тебе жизнью.', '0', '0', '0', '0', 'gilneas'),
('36440', '-1977142', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Земля буквально ушла у меня из-под ног! Я уж думал, мне конец... Спасибо, друг, я твой должник.', '0', '0', '0', '0', 'gilneas'),
('36440', '-1977141', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Я… я думал, мне конец. Спасибо.', '0', '0', '0', '0', 'gilneas'),
('38530', '-1977139', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Это мы еще посмотрим, $N!', '0', '14', '0', '0', 'gilneas'),
('35112', '-1977140', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Конь короля Седогрива', '0', '12', '0', '0', 'gilneas'),
('38530', '-1977138', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'У вас ничего не выйдет!', '0', '0', '0', '0', 'gilneas'),
('38537', '-1977137', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Как пожелаете.', '20918', '12', '0', '0', 'gilneas'),
('38537', '-1977136', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Моя госпожа, мне остановить распространение чумы? Или мы продолжаем действовать по плану?', '20917', '12', '0', '0', 'gilneas'),
('38530', '-1977135', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Что за вопрос? Мы распространим ее, как и запланировали. Пусть люди Гилнеаса насладятся своей маленькой победой. А завтра от них не останется и костей.', '20495', '12', '0', '0', 'gilneas'),
('38530', '-1977134', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ступайте с честью, генерал.', '20494', '12', '0', '0', 'gilneas'),
('38530', '-1977133', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Следите за тоном, генерал. Вам с Гаррошем не о чем беспокоиться. Мы прекратили производство чумы, как и было приказано. Мы никогда бы не стали использовать ее без разрешения.', '20493', '12', '0', '0', 'gilneas'),
('38530', '-1977132', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Можете передать Гаррошу, что это лишь небольшая задержка. Нас ждет верная победа.', '20492', '12', '0', '0', 'gilneas'),
('38533', '-1977131', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Тогда я доложу обо всем Вождю. С Вашего позволения, моя госпожа.', '22622', '12', '0', '0', 'gilneas'),
('38533', '-1977130', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Вы говорите очень убедительно, Ваше Величество. Надеюсь, что вы не собираетесь использовать чуму. Гаррош это категорически запретил.', '22621', '12', '0', '0', 'gilneas'),
('38533', '-1977129', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Гаррош опасается за ситуацию в Гилнеасе. Вы уверены в своих силах, Сильвана?', '22620', '12', '0', '0', 'gilneas'),
('38617', '-1977128', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Убедитесь, что все приготовления завершены. Гонец орков уже ждет Сильвану в соборе.', '0', '14', '0', '0', 'gilneas'),
('38507', '-1977127', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Быстрее, прячься в воде!', '0', '12', '0', '0', 'gilneas'),
('38507', '-1977126', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ты это $gслышал:слышала;, $N? Сильвана направляется к собору. Быстрее, за ней!', '0', '12', '0', '0', 'gilneas'),
('38507', '-1977125', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Постарайся, чтобы нас не заметили, $N.', '0', '12', '0', '0', 'gilneas'),
('38507', '-1977124', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Следуйте за Тобиасом Мистмантлом в Соборный квартал.', '0', '5', '0', '0', 'gilneas'),
('38507', '-1977123', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Идем же, все вместе! Мы выследим, где прячется Сильвана. За Гилнеас!', '0', '12', '0', '0', 'gilneas'),
('38469', '-1977122', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Посмотрим, что бравые гилнеанцы будут делать, оставшись без предводителя!', '0', '1', '0', '0', 'gilneas'),
('38469', '-1977121', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Хватит!', '0', '1', '0', '0', 'gilneas'),
('38470', '-1977120', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ЛИАМ!!! НЕТ!!!', '19727', '1', '0', '0', 'gilneas'),
('38470', '-1977119', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'СИЛЬВАНА!!!', '19726', '1', '0', '0', 'gilneas'),
('38415', '-1977117', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Давай присоединимся к твоему отцу. Ему нужна помощь.', '19500', '1', '0', '0', 'gilneas'),
('38470', '-1977118', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Не дай им уйти, Лиам! Они как раз там, где нам нужно!', '19725', '1', '0', '0', 'gilneas'),
('38415', '-1977116', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Он слишком силен! Стреляйте из катапульт, и он падет!', '19499', '1', '0', '0', 'gilneas'),
('38331', '-1977115', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Гнилокров раздавит хилого воргена!', '0', '1', '0', '0', 'gilneas'),
('38426', '-1977114', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Ни за что на свете я не пропущу эту битву! Вперед!', '19685', '0', '0', '0', 'gilneas'),
('38426', '-1977113', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Люди благодарны за возвращение деревни Тлеющего Угля. Они дали нам кое-что, что бы помочь против Отрекщихся!', '19684', '0', '0', '0', 'gilneas'),
('38218', '-1977112', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Мы вернули свой город... Мы вернули...', '20563', '0', '0', '0', 'gilneas'),
('38218', '-1977111', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Отец, мы победили...', '20562', '0', '0', '0', 'gilneas'),
('38218', '-1977110', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ОТЕЦ!!!', '19612', '1', '0', '0', 'gilneas'),
('38218', '-1977109', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Впереди войска Краули! Поднажмем!', '19611', '1', '0', '0', 'gilneas'),
('38218', '-1977108', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Лорна, как я рад тебя видеть! Солдаты – по пушкам! Остальные выманивают поганищ на открытое пространство!', '19610', '1', '0', '0', 'gilneas'),
('38218', '-1977107', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Поганища перекрывают путь к Военному кварталу! Пробиться будет нелегко.', '19609', '0', '0', '0', 'gilneas'),
('38218', '-1977104', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Атакуйте!', '0', '0', '0', '0', 'gilneas'),
('38218', '-1977105', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Тесни их!', '0', '0', '0', '0', 'gilneas'),
('38218', '-1977106', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'За Гилнеас!', '0', '0', '0', '0', 'gilneas'),
('38218', '-1977102', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ЗА ГИЛНЕАС!', '19651', '1', '0', '0', 'gilneas'),
('38218', '-1977103', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Прошло ваше время, Отрекшиеся мрази!', '0', '0', '0', '0', 'gilneas'),
('38218', '-1977101', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Но мы... НИКОГДА НЕ СДАДИМСЯ!', '19628', '1', '0', '0', 'gilneas'),
('38218', '-1977100', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'А если мы будем зажаты в угол, лишимся сил, оружия и надежды... Мы поднимем голову с презрением и плюнем им в лицо.', '19627', '1', '0', '0', 'gilneas'),
('38218', '-1977099', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Мы будем сражаться в подворотнях, пока с наших костяшек не сойдет кожа, а рапиры не упадут на землю сломанными.', '19626', '1', '0', '0', 'gilneas'),
('38218', '-1977098', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Мы будем сражаться на улицах до последнего патрона. А когда нам нечем будет стрелять, мы будем разбивать их черепа булыжниками с мостовой.', '19625', '1', '0', '0', 'gilneas'),
('38218', '-1977097', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Как же они ошибаются! Мы будем стоять до конца, будем биться, пока у нас есть пушки и солдаты в окопах.', '19624', '1', '0', '0', 'gilneas'),
('38218', '-1977096', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Отрекшиеся думают, что мы ослабли, опустили руки. Они думают, что мы убежим, поджав хвост.', '19623', '1', '0', '0', 'gilneas'),
('37694', '-1977095', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Так это правда? Даже пораженные Проклятием готовы сразиться с Отрекшимися!', '0', '0', '0', '0', 'gilneas'),
('37694', '-1977094', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Спасибо!', '0', '0', '0', '0', 'gilneas'),
('37694', '-1977093', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Отрекшиеся заплатят за свои преступления!', '0', '0', '0', '0', 'gilneas'),
('37875', '-1977092', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Я лучше умру, чем увижу одну из таких тварей на троне!', '19631', '0', '0', '0', 'gilneas'),
('37876', '-1977091', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Все кончено, Годфри. Ни одно восточное баронство больше не поддержит тебя.', '0', '0', '0', '0', 'gilneas'),
('36294', '-1977090', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Люди-скелеты украли знамя Корота! Люди-скелеты поплатятся!!!', '0', '14', '0', '0', 'gilneas'),
('37806', '-1977089', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Пустозвон! Я прикажу пустить твое знамя на тряпки для мытья отхожих мест!', '0', '14', '0', '0', 'gilneas'),
('37065', '-1977088', 'miss content_default', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Вам не пройти, проклятая нежить! Во имя боевого знамени древнего рода Седогривов я изгоняю вас с земли моих предков!', '19606', '14', '0', '0', 'gilneas'),
